    def nb_num
        #Combien d'handle contiennent un numéro ?
        b=$twit.select {|num| num=~/[0-9]/ }
        c=b.count
        puts "Il y a #{c} d'handle qui contiennent un numéro"
    end
    
    def nb_lettre
        #Combien d'handle contiennent les 4 lettres du prénom "Aude" Ã  la suite (sans faire attention Ã  la casse) ? 
        d=$twit.select {|let| /aude/i.match(let)}#.include?("aude")}
        e=d.count
        puts "#{e} d'handle contiennent les 4 lettres du prénom \"Aude\""
    end

    def nb_maj
        #Combien commencent par une majuscule ? 
            maj=$twit.each{|x| x.gsub!(/@/, "")}
            maj2=[]
            for x in maj
                maj2.push(x[1])
            end
            f=maj2.select{|y| /[A-Z]/.match(y)}
        puts "#{f.count} d'handle commence par une majuscule"
    end

    def nb_maj2
        #Combien contiennent une majuscule ?
        m=$twit.select {|x| /[A-Z]/.match(x)}
        puts "#{m.count} d'handle contiennent une majuscule"
    end

    def nb_pseudo
        #Combien de _ dans tous les pseudos confondus ?
        l=$twit.select {|x| x.include?("_")}
        #pseudo=[]

        puts "Il y a #{l.count} de _ dans tous les pseudos confondus"
    end

    def trie
        #Trie la liste de handle par ordre alphabétique 
        m=$twit.sort.each{|x| x.gsub!(/@/, "")}
        puts "Voici le tri de handle par ordre alphabétique :\n #{m}"
    end

    def plus_court
        #Quels sont les 50 handles les plus courts de cette array ?
        court=[]
        for x in $twit
            lg=x.length
            b=lg.to_s.length
            court.push(b<2 ? "0"+lg.to_s+" "+x : lg.to_s+" "+x)
            m=court.sort!{|debut, fin| debut <=> fin}
        end
        puts "Les 50 handles les plus courts de cette array sont : \n #{m[1..50].each{|x| x.gsub!(/[0-9][0-9]\s/, "")}}"

    end

